import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularapp';
  opt1:number;
  opt2:number;
  amt:number;
  res:number;
  result:string;
  exchange (a:number,b:number,amt:number){
    if(a!=null && b!=null){
      this.result=Math.round(b/a*amt).toFixed(2);
      return this.result;
    }
  }
}
