import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularapp';
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Live Forex Conversion</title>
</head>
<body>
    <form id="forexConverter">
        <label for="fromCurrency">Source CCY:</label>
        <select id="fromCurrency" name="fromCurrency">
            <option value="USD">USD</option>
            <option value="GBP">GBP</option>
            <option value="INR">INR</option>
        </select>

        <label for="toCurrency">Target CCY:</label>
        <select id="toCurrency" name="toCurrency">
            <option value="USD">USD</option>
            <option value="GBP">GBP</option>
            <option value="INR">INR</option>
        </select>

        <label for="amount">Amount (Rs):</label>
        <input type="number" id="amount" name="amount" min="0" step="0.01" required>

        <button type="submit" id="submitButton">Convert</button>
    </form>

    <div id="result"></div>

    <script>
        // Exchange rates
        const exchangeRates = {
            "USD": 1.126735,
            "GBP": 0.876893,
            "INR": 79.677056
        };

        const form = document.getElementById("forexConverter");
        const resultContainer = document.getElementById("result");

        form.addEventListener("submit", function (e) {
            e.preventDefault();

            const fromCurrency = document.getElementById("fromCurrency").value;
            const toCurrency = document.getElementById("toCurrency").value;
            const amount = parseFloat(document.getElementById("amount").value);

            if (isNaN(amount)) {
                resultContainer.textContent = "Please enter a valid amount.";
            } else {
                const conversionRate = exchangeRates[toCurrency] / exchangeRates[fromCurrency];
                const convertedAmount = (amount * conversionRate).toFixed(2);

                resultContainer.textContent = `${amount} ${fromCurrency} is equal to ${convertedAmount} ${toCurrency}`;
            }
        });
    </script>
</body>
</html>
