import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularapp';
}
convertedAmount = this.amount;
convertCurrency() {
  this.convertedAmount = (this.amount / this.exchangeRates[this.fromCurrency]) * this.exchangeRates[this.toCurrency];
}
export class ForexConverterComponent {
  exchangeRates = {
    USD: 1.126735,
    GBP: 0.876893,
    INR: 79.677056
  }
}
<div>
    <label for="fromCurrency">Source CCY:</label>
    <select id="fromCurrency" [(ngModel)]="fromCurrency">
      <option value="USD">USD</option>
      <option value="GBP">GBP</option>
      <option value="INR">INR</option>
    </select>
  </div>

  <div>
    <label for="toCurrency">Target CCY:</label>
    <select id="toCurrency" [(ngModel)]="toCurrency">
      <option value="USD">USD</option>
      <option value="GBP">GBP</option>
      <option value="INR">INR</option>
    </select>
  </div>

  <div>
    <label for="amount">Amount (Rs):</label>
    <input type="number" id="amount" [(ngModel)]="amount">
  </div>

  <div>
    <button (click)="convertCurrency()">Convert</button>
  </div>

  <div>
    <p>Result:</p>
    <p>{{ amount }} {{ fromCurrency }} = {{ convertedAmount | number:'1.2-2' }} {{ toCurrency }}</p>
  </div>
