import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class ForexConverterComponent {
  exchangeRates = {
    USD: 1.126735,
    GBP: 0.876893,
    INR: 79.677056
  };

  fromCurrency = 'USD'; // Default source currency
  toCurrency = 'USD'; // Default target currency
  amount = 1; // Default amount

  convertedAmount = this.amount; // Initialize with the default amount

  convertCurrency() {
    this.convertedAmount = (this.amount / this.exchangeRates[this.fromCurrency]) * this.exchangeRates[this.toCurrency];
  }
}
