import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularapp';
}
convertedAmount = this.amount;
<!DOCTYPE html>
<html>
<head>
    <title>Forex Converter</title>
    <script>
        // Define exchange rates
        const exchangeRates = {
            "USD": 1.126735,
            "GBP": 0.876893,
            "INR": 79.677056
        };

        function convertCurrency() {
            // Get user input
            const fromCurrency = document.getElementById("fromCurrency").value;
            const toCurrency = document.getElementById("toCurrency").value;
            const amount = parseFloat(document.getElementById("amount").value);

            // Check if the input is valid
            if (isNaN(amount)) {
                document.getElementById("result").innerHTML = "Please enter a valid amount.";
                return;
            }

            // Perform conversion
            const convertedAmount = (amount / exchangeRates[fromCurrency]) * exchangeRates[toCurrency];

            // Display the result
            document.getElementById("result").innerHTML = `${amount} ${fromCurrency} = ${convertedAmount.toFixed(2)} ${toCurrency}`;
        }
    </script>
</head>
<body>
    <h1>Live Forex Conversion</h1>
    <form>
        <label for="fromCurrency">Source CCY:</label>
        <select id="fromCurrency">
            <option value="USD">USD</option>
            <option value="GBP">GBP</option>
            <option value="INR">INR</option>
        </select>
        <br>

        <label for="toCurrency">Target CCY:</label>
        <select id="toCurrency">
            <option value="USD">USD</option>
            <option value="GBP">GBP</option>
            <option value="INR">INR</option>
        </select>
        <br>

        <label for="amount">Amount (Rs):</label>
        <input type="text" id="amount">
        <br>

        <button type="button" id="submitButton" onclick="convertCurrency()">Convert</button>
    </form>

    <div id="result"></div>
</body>
</html>
