import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  fromCurrency: string = 'USD';
  toCurrency: string = 'USD';
  amount: number = 1;
  convertedAmount: number = 1;

  exchangeRates: { [key: string]: number } = {
    'USD': 1.126735,
    'GBP': 0.876893,
    'INR': 79.677056
  };

  convertCurrency() {
    const fromRate = this.exchangeRates[this.fromCurrency];
    const toRate = this.exchangeRates[this.toCurrency];
    this.convertedAmount = (this.amount / fromRate) * toRate;
  }
}
<div>
  <h1>Live Forex Conversion</h1>
  <form (ngSubmit)="convertCurrency()">
    <label for="fromCurrency">Source CCY:</label>
    <select id="fromCurrency" name="fromCurrency" [(ngModel)]="fromCurrency">
      <option value="USD">USD</option>
      <option value="GBP">GBP</option>
      <option value="INR">INR</option>
    </select>
    <br>

    <label for="toCurrency">Target CCY:</label>
    <select id="toCurrency" name="toCurrency" [(ngModel)]="toCurrency">
      <option value="USD">USD</option>
      <option value="GBP">GBP</option>
      <option value="INR">INR</option>
    </select>
    <br>

    <label for="amount">Amount (Rs):</label>
    <input type="number" id="amount" name="amount" [(ngModel)]="amount">
    <br>

    <button type="submit" id="submitButton">Convert</button>
  </form>

  <div id="result">
    <p>{{ amount }} {{ fromCurrency }} = {{ convertedAmount }} {{ toCurrency }}</p>
  </div>
</div>
